import React from 'react';
import logo from './logo.svg';
import { Label, Note } from "./types"; // Import the Label type from the appropriate module
import { dummyNotesList } from "./constant"; // Import the dummyNotesList from the appropriate module
import ToggleTheme, { ClickCounter } from './hooksExercise';
import { ThemeContext, themes } from "./themeContext";
import { FavoriteNotes } from "./favNotes1";
import './App.css';

function App() {
  return (
    <div className='app-container'>
      <form className="note-form">
        <div><input placeholder="Note Title"></input></div>

        <div><textarea id="content"></textarea></div>

        <div>
          <select name="label" id="label">
            <option value="personal">Personal</option>
            <option value="study">Study</option>
            <option value="work">Work</option>
            <option value="other">Other</option>
          </select>
        </div>

        <div><button type="submit">Create Note</button></div>
      </form>
      <div className="notes-grid">
        {dummyNotesList.map((note) => (
          <div
            key={note.id}
            className="note-item">
            <div className="notes-header">
              <button>x</button>
            </div>
            <h2> {note.title} </h2>
            <p> {note.content} </p>
            <p> {note.label} </p>
          </div>
        ))}
      </div>
      <div className="click-counter">
        <ToggleTheme />
      </div>
   </div>

 );
}

export default App;
